var swisscalc=swisscalc||{};swisscalc.calc=swisscalc.calc||{},swisscalc.calc.tipCalculator=function(){this._subtotal=new swisscalc.display.fixedPointDisplay(2),this._tipPercent=.15},swisscalc.calc.tipCalculator.prototype.getSubtotalValue=function(){return this._subtotal.getDisplayValue()},swisscalc.calc.tipCalculator.prototype.getTipValueDecimal=function(){return this._tipPercent},swisscalc.calc.tipCalculator.prototype.getTipValuePercentage=function(){return 100*this._tipPercent},swisscalc.calc.tipCalculator.prototype.setSubtotalValue=function(t){this._subtotal.setDisplayValue(t)},swisscalc.calc.tipCalculator.prototype.setTipValueDecimal=function(t){this._tipPercent=t},swisscalc.calc.tipCalculator.prototype.setTipValuePercentage=function(t){this._tipPercent=t/100},swisscalc.calc.tipCalculator.prototype.getSubtotalDisplay=function(){return swisscalc.lib.format.asUSCurrency(this._subtotal.getDisplayValue())},swisscalc.calc.tipCalculator.prototype.getTipPercentDisplay=function(){return(100*this._tipPercent).toFixed(1)+"%"},swisscalc.calc.tipCalculator.prototype.getTipAmountDisplay=function(){return swisscalc.lib.format.asUSCurrency(this.getTipAmount())},swisscalc.calc.tipCalculator.prototype.getTipCombinedDisplay=function(){return this.getTipPercentDisplay()+" "+this.getTipAmountDisplay()},swisscalc.calc.tipCalculator.prototype.getTotalDisplay=function(){return swisscalc.lib.format.asUSCurrency(this.getTotal())},swisscalc.calc.tipCalculator.prototype.getTipAmount=function(){var t=this.getSubtotalValue();return t*this._tipPercent},swisscalc.calc.tipCalculator.prototype.getTotal=function(){var t=this.getTipAmount();return this.getSubtotalValue()+t};